mapper.update:
update activity_notified set
status = #{status}
where
notify_id = #{notifyId} and
notified_user_id = #{notifiedUserId}

mapper.select:
select activity_notify.*, activity_notified.status
from activity_notify join activity_notified
on activity_notify.notify_id = activity_notified.notify_id
where activity_notified.notified_user_id = #{userId}
and activity_notified.status = #{status}
limit #{start}, #{offset}
